//为所有调用的imgShort图片增加CLASS

$('.imgShortParent img').each(function(){
			$(this).addClass('imgShort');
});



//动态设定每个单列容器的宽度 = imgLong的宽度，高度 = imgLong的高度，以此来保证图片不会溢出 box-shadow。
